import { useState } from 'react';
import React from 'react';
import * as style from './styles';

export default function ReactionTimeTest() {
  const [colorState, setColorState] = useState(1);
  const [color, setColor] = useState('rgb(43, 135, 209)');

  const red = 'rgb(206, 38, 54)';
  const green = 'rgb(75, 219, 106)';
  const blue = 'rgb(43, 135, 209)';

  const changeToGreen = () => {
    const timerId = setTimeout(() => {
      setColor(green);
      setColorState(3);
    }, 1000);
    return timerId;
  };

  let timerId;
  const handleClick = () => {
    if (colorState == 1) {
      // if blue
      setColor(red); // to red
      setColorState(2); // set to
      timerId = changeToGreen();
      console.log(timerId, 'in blue');
    } else if (colorState == 2) {
      // if red
      console.log(timerId);
      clearTimeout(timerId);
      clearTimeout();
      setColor(blue); // to green
      setColorState(1); // set to blue
    } else {
      // if green
      setColor(blue); // to blue
      setColorState(1); // set to blue
    }
  };

  return (
    <style.Container
      onClick={() => handleClick()}
      style={{ backgroundColor: color }}
    ></style.Container>
  );
}
