import { useState, useRef } from 'react';
import React from 'react';
import * as style from './styles';

export default function Matrix() {
  const [answer, setAnswer] = useState('');
  const [level, setLevel] = useState(1);
  const blockRefs = useRef([]);
  const [question, setQuestion] = useState('123456789');

  const row1 = [1, 2, 3].map((index) => (
    <style.Square
      key={index}
      onClick={(event) => handleBlockClick(event, index)}
      ref={(el) => (blockRefs.current[index] = el)}
    />
  ));
  const row2 = [4, 5, 6].map((index) => (
    <style.Square
      key={index}
      onClick={(event) => handleBlockClick(event, index)}
      ref={(el) => (blockRefs.current[index] = el)}
    />
  ));
  const row3 = [7, 8, 9].map((index) => (
    <style.Square
      key={index}
      onClick={(event) => handleBlockClick(event, index)}
      ref={(el) => (blockRefs.current[index] = el)}
    />
  ));

  const handleBlockClick = (event, index) => {
    event.target.style.backgroundColor = '#fff';
    event.target.style.opacity = 0.8;
    setTimeout(() => {
      event.target.style.backgroundColor = 'rgb(0, 0, 102)';
      event.target.style.opacity = 0.15;
    }, 300);
  };

  const showQuestion = () => {
    for (let i = 0; i < level; i++) {
      blockRefs.current[question[i]];
      setTimeout(() => {
        console.log(blockRefs.current[question[i]].style);
        blockRefs.current[question[i]].style.backgroundColor = 'rgb(0, 0, 102)';
        blockRefs.current[question[i]].style.opacity = 0.15;
      }, 300);
    }
    setLevel((state) => state + 1);
  };

  return (
    <style.Squares>
      <button onClick={() => showQuestion()}>start</button>
      <style.SquareRow>{row1}</style.SquareRow>
      <style.SquareRow>{row2}</style.SquareRow>
      <style.SquareRow>{row3}</style.SquareRow>
    </style.Squares>
  );
}
