import { useState } from 'react';
import React from 'react';
import * as style from './styles';

export default function ReactionTimeTest() {
  const [colorState, setColorState] = useState(1);
  const [color, setColor] = useState('rgb(43, 135, 209)');
  // blue: rgb(43, 135, 209)
  // red : rgb(206, 38, 54)

  const handleClick = () => {
    console.log(colorState);
    if (colorState == 1) {
      // if blue
      setColor('rgb(206, 38, 54)'); // to red
      setColorState(2);
    } else if (colorState == 2) {
      // if red
      setColor('rgb(75, 219, 106)'); // to green
      setColorState(3);
    } else {
      // if green
      setColor('rgb(43, 135, 209)'); // to blue
      setColorState(1);
    }
  };

  return (
    <style.Container
      onClick={handleClick}
      style={{ backgroundColor: color }}
    ></style.Container>
  );
}
