import { useState } from 'react';
import React from 'react';
import * as style from './styles';
import { useNavigate } from 'react-router-dom';

export default function NumberMemoryTest() {
  const navigate = useNavigate();
  const [question, setQuestion] = useState('Click to Start!');
  const [isTesting, setIsTesting] = useState(false);
  const [isTimeOver, setIsTimeOver] = useState(false);
  const [answer, setAnswer] = useState('');

  const startGame = () => {
    setQuestion('6');
    setIsTesting(true);
    setTimeout(() => setIsTimeOver(true), 1000);
  };

  const nextGame = () => {};

  const checkAnswer = (event) => {
    setAnswer(event.target.value);
    console.log(event.target.value);
  };

  return (
    <style.Container onClick={startGame}>
      {isTimeOver ? (
        <div>
          <input onChange={checkAnswer} />
          <button>form 내부의 onSubmit</button>
        </div>
      ) : (
        <style.Question>{question}</style.Question>
      )}
    </style.Container>
  );
}
